con:config.script:
import com.eviware.soapui.support.XmlHolder
def email = context.expand('${Properties#email}')

def firstname = context.expand('${Properties#fname}')

def lastname = context.expand('${Properties#lname}')

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
def request = context.expand('SaveCustomer#Request');
def holder = groovyUtils.getXmlHolder(request);

holder.setNodeValue("//email", email )

holder.setNodeValue("//firstname", firstname )

holder.setNodeValue("//lastname", lastname )
holder.updateProperty()

